  computed: {
    isGridView () {
      return this.$store.state.isGridView
    },
    settings () {
      return this.$store.state.navigator.navigator.settings
    },
    isPropertiesMobileExpanded () {
      return this.$store.state.isPropertiesMobileExpanded
    }
  },
  methods: {
    updateGridView (value) {
      this.$store.commit('basic', { key: 'isGridView', value: value })
      setLocalStorageItem('isGridView', value)
    },
    gotoChildren (value) {
      if (value.path) {
        this.$store.commit('basic', { key: 'mainSectionState', value: 'greed' })
        this.$store.commit('basic', { key: 'greedPath', value: value.path })
        const navElem = {
          name: value.name,
          key: 'greedSource',
          greedPath: value.path,
          value: this.$store.state.navigator.navigator[value.path].items
        }
        this.$store.commit('pushIntoNavStack', navElem)
        this.$store.commit('basic', {
          key: 'greedSource',
          value: this.$store.state.navigator.navigator[value.path].items
        })
        return
      }
      this.$store.commit(TASK.CLEAN_UP_LOADED_TASKS)
      this.$store.dispatch(UID_TO_ACTION[value.link])
      const navElem = {
        name: value.name,
        key: 'taskListSource',
        value: { uid: value.link, param: null }
      }
      this.$store.commit('pushIntoNavStack', navElem)
      this.$store.commit('basic', {
        key: 'taskListSource',
        value: { uid: value.link, param: null }
      })
      this.$store.commit('basic', { key: 'mainSectionState', value: 'tasks' })
    }
  }
}
